uuid_from_bin(au.id) as userid, 
au.email, 
CONVERT_TZ(au.created_at,'GMT','US/Pacific') as u_created_tz, 
CONVERT_TZ(ar.created_at,'GMT','US/Pacific') as r_created_tz,
CONVERT_TZ(DATE_ADD(ar.created_at, INTERVAL 7 DAY),'GMT', 'US/Pacific') as trial_over,
